import SaladCheckbox from "./SaladCheckbox";

export default function SelectExtra({extras, state, stateSetter, extrasError, setErrorState }) {
  const handleChange = (e) => {
    const { name, value, type, checked, id} = e.target;
    let buffer = {...state}
    if (buffer.hasOwnProperty(id)) {
      delete buffer[id]
    } else {
      buffer[id] = true;
    }
    stateSetter(buffer);
  }

  if (Object.keys(extras).length < 3 && Object.keys(extras).length > 9){
    setErrorState(false);
  } else {
    setErrorState(true);
  }

  return (
    <>
      <h3>Tillbeh√∂r: </h3>
      <div className="container-fluid py-4 row">
        {extras.map((x,i) => 
          <SaladCheckbox key={x} x={x[0]} i={i} handleChange={handleChange} state={state[x[0]]} price={x[1].price}></SaladCheckbox>
        )}
      </div>
    </>
  )
}
