async function fetchInventory(url){
  return fetch(url)
  .then(response => {
    if(!response.ok) {
      throw new Error('${url} returned status ${response.status}');
    }
    return response.json();
  });
}

async function fetchIngredient(name) {
  fetchInventory("http://localhost:8080/foundation/" + name);
}

export default async function fetchAllIngredients() {
  const [foundations, proteins, extras, dressings] = await Promise.all([
    fetchInventory("http://localhost:8080/foundations/"),
    fetchInventory("http://localhost:8080/proteins/"), 
    fetchInventory("http://localhost:8080/extras/"),
    fetchInventory("http://localhost:8080/dressings/")
  ]);


  return {
    foundations, 
    proteins, 
    extras, 
    dressings,
  };
}
