import { useState, useMemo } from 'react';

function ComposeSalad(props) {
  const foundations = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].foundation);}, [props.inventory]);
  const [foundation, setFoundation] = useState('Pasta');
  const extras = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].extras);}, [props.inventory]);
  const [extra, setExtra] = useState({ Bacon: true, Fetaost: true });
  const proteins = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].protein);}, [props.inventory]);
  const [protein, setProtein] = useState('Kycklingfilé');
  const dressings = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].dressing);}, [props.inventory]);
  const [dressing, setDressing] = useState('Kycklingfilé');

  return (
    /*<div className="continer col-12">
      <div className="row h-200 p-5 bg-light border rounded-3">
        <h2>Välj bas</h2>
        {foundations.map(name => <div key={name} className="col-4">{name}</div>)}
      </div>
    </div> */

    <form className="form">
      <h2>Välj bas:</h2>
      {foundations.map((x, index) =>
        <>
          <input type="radio" id={x} name="foundation" value={x} key={x}/> 
          <label htmlFor={x}>{x}</label>
          <br/> 
        </>
      )}
      <h2>Välj protein:</h2>
      {proteins.map((x) =>
        <>
          <input type="radio" id={x} name="protein" value={x} key={x}/> 
          <label htmlFor={x}>{x}</label>
          <br/> 
        </>
        )}
      <h2>Välj dressing:</h2>
      {dressings.map((x) =>
        <>
          <input type="radio" id={x} name="dressing" value={x} key={x}/> 
          <label htmlFor={x}>{x}</label>
          <br/> 
        </>
        )}
    </form>
  );
}
export default ComposeSalad;
