import SaladCheckbox from "./SaladCheckbox";

export default function SelectExtra({extras, state, stateSetter}) {
  const handleChange = (e) => {
    e.preventDefault()
    const { name, value, type, checked, id} = e.target;
    let buffer = {...state}
    if (buffer.hasOwnProperty(id)) {
      delete buffer[id]
    } else {
      buffer[id] = true;
    }
    stateSetter(buffer);
  }

  return (
    <>
      <h3>Tillbeh√∂r: </h3>
      <div className="container-fluid py-4 row">
        {extras.map((x,i) => 
          <SaladCheckbox key={i} x={x[0]} i={i} handleChange={handleChange} state={state[x]} price={x[1].price}></SaladCheckbox>
        )}
      </div>
    </>
  )
}
