import React, { useState, useMemo } from 'react';
import ComposeSalad from './ComposeSalad';
import inventory from './inventory.mjs';
import Salad from './Salad';

  function SalladForm(props) {
    const foundations = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].foundation);}, [props.inventory]);
    const [foundation, setFoundation] = useState('Pasta');
    const extras = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].extra);}, [props.inventory]);
    const [extra, setExtra] = useState({ Bacon: true, Fetaost: true });
    const proteins = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].protein);}, [props.inventory]);
    const [protein, setProtein] = useState('Kycklingfilé');
    const dressings = useMemo(() => {return Object.keys(props.inventory).filter(name => props.inventory[name].dressing);}, [props.inventory]);
    const [dressing, setDressing] = useState('Kimchimayo');
    const salads = useState([Salad]);

    const handleChange = (e) => {

      const { name, value, type, checked, id} = e.target;
      switch (name) {
        case 'foundation':
          setFoundation(value);
          break;
        case 'protein':
          setProtein(value);
          break;
        case 'extra':
          let buffer = {...extra}
          if (buffer.hasOwnProperty(id)) {
            delete buffer[id]
          } else {
            buffer[id] = true;
          }
          console.log(buffer)
          setExtra(buffer);
          break;
        case 'dressing':
          setDressing(value);
          break;
      }
      //e.preventDefault();
    }

    function handleSubmit(e) {
      e.preventDefault();
      console.log("här: " + foundation, protein, extra, dressing)
    }

    function createSalad(foundation, protein, extra, dressing) {
      let newSalad = new Salad().add(foundation)
                            .add(protein)
                            .add(extra)
                            .add(dressing);
      return newSalad;                    
    }

    return (
      <React.Fragment>
      <form name="form" key="salladForm" onSubmit={handleSubmit}>
        <h2 key="basHeader">Välj bas:</h2>
        <select key="bas" name="foundation"  onChange={handleChange} value={foundation}>
          <option selected=""></option>
          {foundations.map((x, i) => <option key={i} value={x}>{x}</option>)}
        </select>

        <h2 key="proteinHeader">Välj protein:</h2>
        <select key="protein" name="protein" onChange={handleChange} value={protein}>
          <option selected=""></option>
          {proteins.map((x, i) => <option key={i} value={x}>{x}</option>)}
        </select>

        <h2 key="extraHeader">Välj tillbehör:</h2>
        {extras.map((x, i) =>
          <React.Fragment key={i}>
            <input
              type="checkbox"
              id={x}
              name="extra"
              onChange={handleChange}
              checked={extra[x] || false} 
            />
            <label htmlFor={x}>{x}</label>
            <br />
          </React.Fragment>
        )}

        <h2 key="dressingHeader">Dressing:</h2>
        <select key="dressing" name="dressing" value={dressing} onChange={handleChange}>
          <option selected=""></option>
          {dressings.map((x, i) => <option key={i} value={x}>{x}</option>)}
        </select>

        <br />
        <br />
        <button type="submit">Lägg i varukorg</button>
      </form>
        <div>
          <h1>Varukorg</h1>
          <ul>
            <li>Foundation: {foundation}</li>
            <li>Protein: {protein}</li>
            <li>Extras: {(Object.keys(extra)).join(", ")}</li>
            <li>Dressing: {dressing}</li>
          </ul>
        </div>
    </React.Fragment>
  );
}

  export default SalladForm;
