    let buffer = {...state}
    if (buffer.hasOwnProperty(id)) {
      delete buffer[id]
    } else {
      buffer[id] = true;
    }
    console.log(buffer)
    stateSetter(buffer);
  }

  console.log(      options.map((x,i) => 
    <SaladCheckbox x={x} i={i} handleChange={handleChange} state={state[x]}></SaladCheckbox>
  ))
  return (
    <>
      {options.map((x,i) => 
        <SaladCheckbox x={x} i={i} handleChange={handleChange} state={state[x]}></SaladCheckbox>
      )}
    </>
  )
}

